async function getIPInfo(){try{document.getElementById("ip-address").classList.add("loading"),document.getElementById("location").classList.add("loading"),document.getElementById("isp").classList.add("loading"),document.getElementById("type").classList.add("loading");const i=await fetch("https://ipapi.co/json/"),e=await i.json(),t=document.getElementById("ip-address"),n=document.getElementById("location"),s=document.getElementById("isp"),o=document.getElementById("type");t.textContent=e.ip,n.textContent=`${e.city}, ${e.country_name}`,s.textContent=e.org,o.textContent=e.version==="IPv4"?"IPv4":"IPv6",t.classList.remove("loading"),n.classList.remove("loading"),s.classList.remove("loading"),o.classList.remove("loading")}catch(e){console.error("Error fetching IP info:",e),document.getElementById("ip-address").textContent="Error loading IP",document.getElementById("location").textContent="Not available",document.getElementById("isp").textContent="Not available",document.getElementById("type").textContent="Not available"}}document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("refresh-ip");e.addEventListener("click",getIPInfo),getIPInfo()})