async function getIPInfo(){document.getElementById("loading").style.display="block",document.getElementById("ip-result").style.display="none";try{const s=await fetch("https://api.ipify.org?format=json"),t=await s.json(),o=await fetch(`https://ipapi.co/${t.ip}/json/`),e=await o.json();let n="-";try{const e=await fetch("https://api64.ipify.org?format=json"),t=await e.json();n=t.ip}catch{console.log("IPv6 not available")}document.getElementById("ipv4").textContent=t.ip,document.getElementById("ipv6").textContent=n,document.getElementById("country").textContent=e.country_name,document.getElementById("city").textContent=e.city,document.getElementById("region").textContent=e.region,document.getElementById("isp").textContent=e.org,document.getElementById("org").textContent=e.org,document.getElementById("timezone").textContent=e.timezone,document.getElementById("user-agent").textContent=navigator.userAgent,document.getElementById("language").textContent=navigator.language,document.getElementById("loading").style.display="none",document.getElementById("ip-result").style.display="grid"}catch{document.getElementById("loading").innerHTML='<div class="error">Error fetching IP information. Please try again.</div>'}}function refreshIPInfo(){getIPInfo()}document.addEventListener("DOMContentLoaded",getIPInfo)